import React, { useEffect, useState } from "react";
import { useLocation, useParams } from "react-router-dom";
import './Weather.css';

const Weather = (props) => {
    // const [finalDataa, setFinalData] = useState([]);
    const { dt } = useParams();
    const location = useLocation();
    console.log(location);

    const dtText = [];
    const [image, setImage] = useState();

    const checkDtText = (dt) => {
        location.state.map((item) => {
            if (dt == item.dt) {
                dtText.push(item);
            }
        })
    }

    const fetchImage = () => {
        fetch(`http://api.openweathermap.org/geo/1.0/direct?q=Boston&limit=5&appid=7e645c5e4585ced4f48d0c5bcfc758c3`)
            .then(res => res.json())
            .then(result => {
                fetch(`http://api.openweathermap.org/data/2.5/forecast?lat=${result[0].lat}&lon=${result[0].lon}&appid=7e645c5e4585ced4f48d0c5bcfc758c3`)
                    .then(newRes => newRes.json())
                    .then(newData => {
                        console.log(newData.list);
                        setFinalData(newData.list);
                    })
            })
    }






    return (
        <div>
            {checkDtText(dt)}
            {dtText.map((data) => {
                return (
                    <div className="weatherWrapper">
                        <div className="dateAndTimeWapper">
                            <h2>Date and Time: {data.dt_txt}</h2>
                        </div>
                        <div className="desciptionWrapper">
                            <div className="description">
                                <h2>Description: {data.weather[0].description}</h2>
                            </div>
                        </div>
                        <div className="tempWrapper">
                            <div className="temp">
                                <h2>Temp: {data.main.temp}</h2>
                            </div>
                            <div className="maxTemp">
                                <h2>Max-Temp: {data.main.temp_max}</h2>
                            </div>
                            <div className="minTemp">
                                <h2>Min-Temp: {data.main.temp_min}</h2>
                            </div>
                            <div className="feelsLikeTemp">
                                <h2>Feels Like : {data.main.feels_like}</h2>
                            </div>
                        </div>
                    </div>
                );
            })}
        </div>
    );


}

