import React, { useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";
import './Weather.css';

const Weather = (props) => {
    const { id } = useParams();
    const location = useLocation();
    console.log(location);

    const checkWeather = () => {
        props.stateWeather.map((item) => {
            return {
                name: item.name,
                lat: item.lat
            }
        });
    }

    const finalData = [];

    const checkId = (id) => {
        location.state.data.map((item) => {
            if (id == item.id) {
                console.log('found');
                finalData.push(item);
                console.log(item);
            }
            else {
                console.log('Not found');
            }
        })
    }

    useEffect(() => {
        checkWeather();
    }
        , [id]);

    return (
        <div>
            {checkId(id)}
            {finalData.map((item) => {
                return (
                    <div className="finalWeatherWrapper">
                        <div className="finalDayWrapper">
                            {item.day}
                        </div>
                        <div className="finalDegreeWrapper">
                            {item.degrees}
                        </div>
                        <div className="finalStatusWrapper">
                            {item.status}
                        </div>
                    </div>
                );
            })}
        </div>
    );
}

export default Weather;
