import React, { useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";
import './Weather.css';

const Weather = (props) => {
    const { id } = useParams();
    const location = useLocation();
    console.log(location);


    const finalData = [];

    const checkId = (id) => {
        location.state.data.map((item) => {
            if (id == item.id) {
                console.log('found');
                finalData.push(item);
                console.log(item);
            }
            else {
                console.log('Not found');
            }
        })
    }

    const fetchData = () => {
        fetch(`http://api.openweathermap.org/geo/1.0/direct?q=Boston&limit=5&appid=7e645c5e4585ced4f48d0c5bcfc758c3`)
          .then(res => res.json())
          .then(result => {
            fetch(`http://api.openweathermap.org/data/2.5/forecast?lat=${result[0].lat}&lon=${result[0].lon}&appid=7e645c5e4585ced4f48d0c5bcfc758c3`)
              .then(newRes => newRes.json())
              .then(newData => {
                console.log(newData.list);
                setFinalData(newData);
              })
          })
      }

    useEffect(() => {
        fetchData();
    }, []);

    return (
        <div>
            {checkId(id)}
            {finalData.map((item) => {
                return (
                    <div className="finalWeatherWrapper">
                        <div className="finalDayWrapper">
                            {item.day}
                        </div>
                        <div className="finalDegreeWrapper">
                            {item.degrees}
                        </div>
                        <div className="finalStatusWrapper">
                            {item.status}
                        </div>
                    </div>
                );
            })}
            {props.weatherFinalData.map((item) => {
                return (
                    <div key={Math.random().toString()}>
                        <div>
                            <h2>{item.list.dt_txt}</h2>
                        </div>
                    </div>
                );
            })}
        </div>
    );
}

export default Weather;
